import logo from './logo.svg';
import './App.css';
import { Grid } from 'gridjs-react';

function App() {
  const grid = new Grid({
    search: {
      server: {
        url: (prev, keyword) => `${prev}?search=${keyword}`
      }
    },
    columns: ['Title', 'Director', 'Producer'],
    server: {
      url: 'https://swapi.dev/api/films/',
      then: console.log("HELLO")
    } 
  });
  return grid;
}

export default App;
