import logo from './logo.svg';
import './App.css';
import { Grid } from 'gridjs-react';
import { h } from 'gridjs';

function App() {

  const baseUrl = 'http://localhost:8080/api'; // Base URL of the server

  // Define endpoint and parameters
  const endpoint = '/sheet';
  const queryParams = {
    limit: 10
  };

  // Construct the URL with endpoint and parameters
  const sheetUrl = new URL(baseUrl + endpoint);
  Object.keys(queryParams).forEach(key => sheetUrl.searchParams.append(key, queryParams[key]));

  console.log(sheetUrl.toString());

  const columns = [
    { name: '1', attributes:{ 
      "contenteditable" : "false"
  }, formatter: (cell) => h('div', { style: { backgroundColor: '#f0f0f0', fontWeight: 'bold', height: '100%', width: '100%', boxSizing: 'border-box', borderCollapse: 'collapse'} }, cell), // Custom rendering for shading
      }, 
    ...Array.from({ length: 100 - 1 }, (_, index) => ({
      name: `${index + 0}`,
      attributes:{ 
        "contenteditable" : "true"
    }, formatter: (cell) => h('div', { style: { height: '100%', width: '100%', boxSizing: 'border-box', borderCollapse: 'collapse', width: '40px'} }, cell), resizable: true,
  }))
  ];

  const data = Array.from({ length: 100 }, (_, rowIndex) => {
    const row = Array.from({ length: 100 - 1 }, () => '');
    return [rowIndex + 0, ...row]; 
  });


  const grid = new Grid({
    columns: columns,
    data:data,
    style: {
      borderCollapse: 'collapse', // Remove gap between cells
      border: '1px solid #ddd', // Add border to every row and column
      table: {
        border: '1px solid #ccc',
        borderCollapse: 'collapse'
      },
      th: {
        'background-color': '#f0f0f0',
        color: '#000',
        'text-align': 'center',
        border: '1px solid #ccc',
        borderCollapse: 'collapse'
      },
      td: {
        'text-align': 'center',
        border: '1px solid #ccc',
        borderCollapse: 'collapse'
      }
    },
    search: {
      server: {
        url: (prev, keyword) => `${prev}?search=${keyword}`
      }
    },
    server: {
      url: sheetUrl,
      then: data => {
        const columns = Array.from(Array(101).keys()).map(String);
        data: data.results.map(inp => columns.map(column => inp[column]))
        catchError: error => {
          // Handling of errors, if any
          console.error('Error fetching data:', error);
        }
      }
    } 
  });
  return grid;
}

export default App;
