              )}
            >
              ...
            </button>
          </Fragment>
        )}

        {Array.from(Array(maxCount).keys())
          .map((i) => currentPage + (i - pagePivot))
          .map((i) => (
            <button
              tabIndex={0}
              role="button"
              onClick={() => setPage(i)}
              className={classJoin(
                currentPage === i
                  ? classJoin(
                      className('currentPage'),
                      config.className.paginationButtonCurrent,
                    )
                  : null,
                config.className.paginationButton,
              )}
              title={_('pagination.page', i + 1)}
              aria-label={_('pagination.page', i + 1)}
            >
              {_(`${i + 1}`)}
            </button>
          ))}

        {pages() > maxCount && pages() > currentPage + pagePivot + 1 && (
          <Fragment>
            <button
              tabIndex={-1}
              className={classJoin(
                className('spread'),
                config.className.paginationButton,
              )}
            >
              ...
            </button>
            <button
              tabIndex={0}
              role="button"
              onClick={() => setPage(pages() - 1)}
              title={_('pagination.page', pages())}
              aria-label={_('pagination.page', pages())}
              className={config.className.paginationButton}
            >
              {_(`${pages()}`)}
            </button>
          </Fragment>
        )}
      </Fragment>
    );
  };

  const renderSummary = () => {
    return (
      <Fragment>
        {summary && total > 0 && (
          <div
            role="status"
            aria-live="polite"
            className={classJoin(
              className('summary'),
              config.className.paginationSummary,
            )}
            title={_('pagination.navigate', currentPage + 1, pages())}
          >
            {_('pagination.showing')} <b>{_(`${currentPage * limit + 1}`)}</b>{' '}
            {_('pagination.to')}{' '}
            <b>{_(`${Math.min((currentPage + 1) * limit, total)}`)}</b>{' '}
            {_('pagination.of')} <b>{_(`${total}`)}</b>{' '}
            {_('pagination.results')}
          </div>
        )}
      </Fragment>
    );
  };

  return (
    <div
      className={classJoin(
        className('pagination'),
        config.className.pagination,
      )}
    >
      {renderSummary()}

      <div className={className('pages')}>
        {prevButton && (
          <button
            tabIndex={0}
            role="button"
            disabled={currentPage === 0}
            onClick={() => setPage(currentPage - 1)}
            title={_('pagination.previous')}
            aria-label={_('pagination.previous')}
            className={classJoin(
              config.className.paginationButton,
              config.className.paginationButtonPrev,
            )}
          >
            {_('pagination.previous')}
          </button>
        )}

        {renderPages()}

        {nextButton && (
          <button
            tabIndex={0}
            role="button"
            disabled={pages() === currentPage + 1 || pages() === 0}
            onClick={() => setPage(currentPage + 1)}
            title={_('pagination.next')}
            aria-label={_('pagination.next')}
            className={classJoin(
              config.className.paginationButton,
              config.className.paginationButtonNext,
            )}
          >
            {_('pagination.next')}
          </button>
        )}
      </div>
    </div>
  );
}
export function width(width: string | number, containerWidth?: number): number {
  if (typeof width == 'string') {
    if (width.indexOf('%') > -1) {
      return (containerWidth / 100) * parseInt(width, 10);
    } else {
      return parseInt(width, 10);
    }
  }

  return width;
}

export function px(width: number): string {
  if (!width) return '';
  return `${Math.floor(width)}px`;
}
import { h } from 'preact';
import { className } from '../../util/className';

/**
 * ShadowTable renders a hidden table and is used to calculate the column's width
 * when autoWidth option is enabled
 */
export function ShadowTable(props: { tableRef: HTMLTableElement }) {
  const shadowTable = props.tableRef.cloneNode(true) as HTMLTableElement;

  shadowTable.style.position = 'absolute';
  shadowTable.style.width = '100%';
  shadowTable.style.zIndex = '-2147483640';
  shadowTable.style.visibility = 'hidden';

  return (
    <div
      ref={(nodeElement) => {
        nodeElement && nodeElement.appendChild(shadowTable);
      }}
    />
  );
}

export function getShadowTableWidths(tempRef: HTMLDivElement): {
  [columnId: string]: { minWidth: number; width: number };
} {
  const tableElement: HTMLTableElement = tempRef.querySelector(
    'table',
  ) as HTMLTableElement;

  if (!tableElement) {
    return {};
  }

  const tableClassName = tableElement.className;
  const tableStyle = tableElement.style.cssText;
  tableElement.className = `${tableClassName} ${className('shadowTable')}`;

  tableElement.style.tableLayout = 'auto';
  tableElement.style.width = 'auto';
  tableElement.style.padding = '0';
  tableElement.style.margin = '0';
  tableElement.style.border = 'none';
  tableElement.style.outline = 'none';

  let obj = Array.from(
    tableElement.parentNode.querySelectorAll<HTMLElement>('thead th'),
  ).reduce((prev, current) => {
    current.style.width = `${current.clientWidth}px`;

    return {
      [current.getAttribute('data-column-id')]: {
        minWidth: current.clientWidth,
      },
      ...prev,
    };
  }, {});

  tableElement.className = tableClassName;
  tableElement.style.cssText = tableStyle;
  tableElement.style.tableLayout = 'auto';

  obj = Array.from(
    tableElement.parentNode.querySelectorAll<HTMLElement>('thead th'),
  ).reduce((prev, current) => {
    prev[current.getAttribute('data-column-id')]['width'] = current.clientWidth;

    return prev;
  }, obj);

  return obj;
}
export function camelCase(str: string): string {
  if (!str) return '';

  const words = str.split(' ');

  // do not convert strings that are already in camelCase format
  if (words.length === 1 && /([a-z][A-Z])+/g.test(str)) {
    return str;
  }

  return words
    .map(function (word, index) {
      // if it is the first word, lowercase all the chars
      if (index == 0) {
        return word.toLowerCase();
      }

      // if it is not the first word only upper case the first char and lowercase the rest
      return word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
    })
    .join('');
}
import { Fragment, FunctionComponent, h } from 'preact';
import { useConfig } from './hooks/useConfig';
import log from './util/log';

export enum PluginPosition {
  Header,
  Footer,
  Cell,
}

export interface Plugin<T extends FunctionComponent> {
  id: string;
  position: PluginPosition;
  component: T;
  order?: number;
}

export class PluginManager {
  private readonly plugins: Plugin<any>[];

  constructor() {
    this.plugins = [];
  }

  get<T extends FunctionComponent>(id: string): Plugin<T> | undefined {
    return this.plugins.find((p) => p.id === id);
  }

  add<T extends FunctionComponent<any>>(plugin: Plugin<T>): this {
    if (!plugin.id) {
      log.error('Plugin ID cannot be empty');
      return this;
    }

    if (this.get(plugin.id)) {
      log.error(`Duplicate plugin ID: ${plugin.id}`);
      return this;
    }

    this.plugins.push(plugin);
    return this;
  }

  remove(id: string): this {
    const plugin = this.get(id);

    if (plugin) {
      this.plugins.splice(this.plugins.indexOf(plugin), 1);
    }

    return this;
  }

  list<T extends FunctionComponent>(position?: PluginPosition): Plugin<T>[] {
    let plugins: Plugin<T>[];

    if (position != null || position != undefined) {
      plugins = this.plugins.filter((p) => p.position === position);
    } else {
      plugins = this.plugins;
    }

    return plugins.sort((a, b) => (a.order && b.order ? a.order - b.order : 1));
  }
}

export function PluginRenderer(props: {
  props?: any;
  // to render a single plugin
  pluginId?: string;
  // to render all plugins in this PluginPosition
  position?: PluginPosition;
}) {
  const config = useConfig();

  if (props.pluginId) {
    // render a single plugin
    const plugin = config.plugin.get(props.pluginId);

    if (!plugin) return null;

    return h(
      Fragment,
      {},
      h(plugin.component, {
        plugin: plugin,
        ...props.props,
      }),
    );
  } else if (props.position !== undefined) {
    // render using a specific plugin position
    return h(
      Fragment,
      {},
      config.plugin.list(props.position).map((p) => {
        return h(p.component, { plugin: p, ...this.props.props });
      }),
    );
  }

  return null;
}
/**
 * Centralized logging lib
 *
 * This class needs some improvements but so far it has been used to have a coherent way to log
 */
class Logger {
  private format(message: string, type: string): string {
    return `[Grid.js] [${type.toUpperCase()}]: ${message}`;
  }

  error(message: string, throwException = false): void {
    const msg = this.format(message, 'error');

    if (throwException) {
      throw Error(msg);
    } else {
      console.error(msg);
    }
  }

  warn(message: string): void {
    console.warn(this.format(message, 'warn'));
  }

  info(message: string): void {
    console.info(this.format(message, 'info'));
  }
}

