import { TData } from '../types';

abstract class Storage<I> {
  /**
   * Returns all rows based on ...args
   * @param args
   */
  abstract get(...args): Promise<StorageResponse>;

  /**
   * To set all rows
   *
   * @param data
   */
  set?(data: I | ((...args) => void)): this;
}

export interface StorageResponse {
  data: TData;
  total: number;
}

export default Storage;
import Storage, { StorageResponse } from './storage';
import { TData } from '../types';

class MemoryStorage extends Storage<TData> {
  private data: (() => TData) | (() => Promise<TData>);

  constructor(data: TData | (() => TData) | (() => Promise<TData>)) {
    super();
    this.set(data);
  }

  public async get(): Promise<StorageResponse> {
    const data = await this.data();

    return {
      data: data,
      total: data.length,
    };
  }

  public set(data: TData | (() => TData) | (() => Promise<TData>)): this {
    if (data instanceof Array) {
      this.data = (): TData => data;
    } else if (data instanceof Function) {
      this.data = data;
    }

    return this;
  }
}

export default MemoryStorage;
import Storage, { StorageResponse } from './storage';
import log from '../util/log';

export interface ServerStorageOptions extends RequestInit {
  url: string;
  // to format the data and columns
  then?: (data: any) => any[][];
  // to handle the response from the server. `handle` will
  // be called first and then `then` callback will be invoked
  // The purpose of this function is to handle the behaviour
  // of server and either reject and resolve the initial response
  // before calling the `then` function
  handle?: (response: Response) => Promise<any>;
  total?: (data: any) => number;
  // to bypass the current implementation of ServerStorage and process the
  // request manually (e.g. when user wants to connect their own SDK/HTTP Client)
  data?: (opts: ServerStorageOptions) => Promise<StorageResponse>;
}

class ServerStorage extends Storage<ServerStorageOptions> {
  private readonly options: ServerStorageOptions;

  constructor(options: ServerStorageOptions) {
    super();
    this.options = options;
  }

  private handler(response: Response): Promise<any> {
    if (typeof this.options.handle === 'function') {
      return this.options.handle(response);
    }

    if (response.ok) {
      return response.json();
    } else {
      log.error(
        `Could not fetch data: ${response.status} - ${response.statusText}`,
        true,
      );
      return null;
    }
  }

  public get(options?: ServerStorageOptions): Promise<StorageResponse> {
    // this.options is the initial config object
    // options is the runtime config passed by the pipeline (e.g. search component)
    const opts = {
      ...this.options,
      ...options,
    };

    // if `options.data` is provided, the current ServerStorage
    // implementation will be ignored and we let options.data to
    // handle the request. Useful when HTTP client needs to be
    // replaced with something else
    if (typeof opts.data === 'function') {
      return opts.data(opts);
    }

    return fetch(opts.url, opts)
      .then(this.handler.bind(this))
      .then((res) => {
        return {
          data: opts.then(res),
          total: typeof opts.total === 'function' ? opts.total(res) : undefined,
        };
      });
  }
}

export default ServerStorage;
import { Config } from '../config';
import MemoryStorage from './memory';
import Storage from './storage';
import ServerStorage from './server';
import log from '../util/log';
import { decode, html } from '../util/html';

class StorageUtils {
  /**
   * Accepts a Config object and tries to guess and return a Storage type
   *
   * @param config
   */
  public static createFromConfig(config: Config): Storage<any> {
    let storage = null;
    // `data` array is provided
    if (config.data) {
      storage = new MemoryStorage(config.data);
    }

    if (config.from) {
      storage = new MemoryStorage(this.tableElementToArray(config.from));
      // remove the source table element from the DOM
      config.from.style.display = 'none';
    }

    if (config.server) {
      storage = new ServerStorage(config.server);
    }

    if (!storage) {
      log.error('Could not determine the storage type', true);
    }

    return storage;
  }

  /**
   * Accepts a HTML table element and converts it into a 2D array of data
   *
   * TODO: This function can be a step in the pipeline: Convert Table -> Load into a memory storage -> ...
   *
   * @param element
   */
  static tableElementToArray(element: HTMLElement): any[][] {
    const arr = [];
    const tbody = element.querySelector('tbody');
    const rows = tbody.querySelectorAll('tr');

    for (const row of rows as any) {
      const cells: HTMLElement[] = row.querySelectorAll('td');
      const parsedRow = [];

      for (const cell of cells) {
        // try to capture a TD with single text element first
        if (
          cell.childNodes.length === 1 &&
          cell.childNodes[0].nodeType === Node.TEXT_NODE
        ) {
          parsedRow.push(decode(cell.innerHTML));
        } else {
          parsedRow.push(html(cell.innerHTML));
        }
      }

      arr.push(parsedRow);
    }

    return arr;
  }
}

export default StorageUtils;
import { PipelineProcessor, ProcessorType } from './processor';
import { ID } from '../util/id';
import log from '../util/log';
