export class Config {
  public constructor() {
    Object.assign(this, Config.defaultConfig());
  }
  /**
   * Assigns `updatedConfig` keys to the current config file
   *
   * @param partialConfig
   */
  assign(partialConfig: Partial<Config>): Config {
    return Object.assign(this, partialConfig);
  }

  /**
   * Updates the config from a partial Config
   *
   * @param partialConfig
   */
  update(partialConfig: Partial<Config>): Config {
    if (!partialConfig) return this;

    this.assign(
      Config.fromPartialConfig({
        ...this,
        ...partialConfig,
      }),
    );

    return this;
  }

  static defaultConfig(): Partial<Config> {
    return {
      store: new Store({
        status: Status.Init,
        header: undefined,
        data: null,
      }),
      plugin: new PluginManager(),
      tableRef: createRef(),
      width: '100%',
      height: 'auto',
      processingThrottleMs: 100,
      autoWidth: true,
      style: {},
      className: {},
    };
  }

  static fromPartialConfig(partialConfig: Partial<Config>): Partial<Config> {
    const config = new Config().assign(partialConfig);

    // Sort
    if (typeof partialConfig.sort === 'boolean' && partialConfig.sort) {
      config.assign({
        sort: {
          multiColumn: true,
        },
      });
    }

    // Header
    config.assign({
      header: Header.createFromConfig(config),
    });

    config.assign({
      storage: StorageUtils.createFromConfig(config),
    });

    config.assign({
      pipeline: PipelineUtils.createFromConfig(config),
    });

    // Translator
    config.assign({
      translator: new Translator(config.language),
    });

    // clear existing plugins list to prevent duplicate errors
    config.plugin = new PluginManager();

    if (config.search) {
      // Search
      config.plugin.add({
        id: 'search',
        position: PluginPosition.Header,
        component: Search,
      });
    }

    if (config.pagination) {
      // Pagination
      config.plugin.add({
        id: 'pagination',
        position: PluginPosition.Footer,
        component: Pagination,
      });
    }

    // Additional plugins
    if (config.plugins) {
      config.plugins.forEach((p) => config.plugin.add(p));
    }

    return config;
  }
}
import { h, ComponentChild, JSX } from 'preact';

import Cell from '../../cell';
import { classJoin, className } from '../../util/className';
import { CSSDeclaration, TColumn } from '../../types';
import Row from '../../row';
import { JSXInternal } from 'preact/src/jsx';
import { PluginRenderer } from '../../plugin';
import { useConfig } from '../../hooks/useConfig';

export function TD(
  props: {
    cell: Cell;
    row?: Row;
    column?: TColumn;
    style?: CSSDeclaration;
    messageCell?: boolean;
  } & Omit<JSX.HTMLAttributes<HTMLTableCellElement>, 'style'>,
) {
  const config = useConfig();

  const content = (): ComponentChild => {
    if (props.column && typeof props.column.formatter === 'function') {
      return props.column.formatter(props.cell.data, props.row, props.column);
    }

    if (props.column && props.column.plugin) {
      return (
        <PluginRenderer
          pluginId={props.column.id}
          props={{
            column: props.column,
            cell: props.cell,
            row: props.row,
          }}
        />
      );
    }

    return props.cell.data;
  };

  const handleClick = (
    e: JSX.TargetedMouseEvent<HTMLTableCellElement>,
  ): void => {
    if (props.messageCell) return;

    config.eventEmitter.emit(
      'cellClick',
      e,
      props.cell,
      props.column,
      props.row,
    );
  };

  const getCustomAttributes = (
    column: TColumn | null,
  ): JSXInternal.HTMLAttributes<HTMLTableCellElement> => {
    if (!column) return {};

    if (typeof column.attributes === 'function') {
      return column.attributes(props.cell.data, props.row, props.column);
    } else {
      return column.attributes;
    }
  };

  return (
    <td
      role={props.role}
      colSpan={props.colSpan}
      data-column-id={props.column && props.column.id}
      className={classJoin(
        className('td'),
        props.className,
        config.className.td,
      )}
      style={{
        ...props.style,
        ...config.style.td,
      }}
      onClick={handleClick}
      {...getCustomAttributes(props.column)}
    >
      {content()}
    </td>
  );
}
import { h, JSX, ComponentChildren } from 'preact';

import Row from '../../row';
import Cell from '../../cell';
import { classJoin, className } from '../../util/className';
import { TColumn } from '../../types';
import { TD } from './td';
import Header from '../../header';
import { useConfig } from '../../hooks/useConfig';
import useSelector from '../../hooks/useSelector';

export function TR(props: {
  row?: Row;
  messageRow?: boolean;
  children?: ComponentChildren;
}) {
  const config = useConfig();
  const header = useSelector((state) => state.header);

  const getColumn = (cellIndex: number): TColumn => {
    if (header) {
      const cols = Header.leafColumns(header.columns);

      if (cols) {
        return cols[cellIndex];
      }
    }

    return null;
  };

  const handleClick = (
    e: JSX.TargetedMouseEvent<HTMLTableRowElement>,
  ): void => {
    if (props.messageRow) return;
    config.eventEmitter.emit('rowClick', e, props.row);
  };

  const getChildren = (): ComponentChildren => {
    if (props.children) {
      return props.children;
    }

    return props.row.cells.map((cell: Cell, i) => {
      const column = getColumn(i);

      if (column && column.hidden) return null;

      return <TD key={cell.id} cell={cell} row={props.row} column={column} />;
    });
  };

  return (
    <tr
      className={classJoin(className('tr'), config.className.tr)}
      onClick={handleClick}
    >
      {getChildren()}
    </tr>
  );
}
