export function THead() {
  const config = useConfig();
  const header = useSelector((state) => state.header);

  const renderColumn = (
    column: TColumn,
    rowIndex: number,
    columnIndex: number,
    totalRows: number,
  ) => {
    const { rowSpan, colSpan } = calculateRowColSpans(
      column,
      rowIndex,
      totalRows,
    );

    return (
      <TH
        column={column}
        index={columnIndex}
        colSpan={colSpan}
        rowSpan={rowSpan}
      />
    );
  };

  const renderRow = (row: TColumn[], rowIndex: number, totalRows: number) => {
    // because the only sortable columns are leaf columns (not parents)
    const leafColumns = Header.leafColumns(header.columns);

    return (
      <TR>
        {row.map((col) => {
          if (col.hidden) return null;

          return renderColumn(
            col,
            rowIndex,
            leafColumns.indexOf(col),
            totalRows,
          );
        })}
      </TR>
    );
  };

  const renderRows = () => {
    const rows = Header.tabularFormat(header.columns);

    return rows.map((row, rowIndex) => renderRow(row, rowIndex, rows.length));
  };

  if (header) {
    return (
      <thead
        key={header.id}
        className={classJoin(className('thead'), config.className.thead)}
      >
        {renderRows()}
      </thead>
    );
  }

  return null;
}
import { TColumn } from '../types';
import Header from '../header';

export function calculateRowColSpans(
  column: TColumn,
  rowIndex: number,
  totalRows: number,
): { rowSpan: number; colSpan: number } {
  const depth = Header.maximumDepth(column);
  const remainingRows = totalRows - rowIndex;
  const rowSpan = Math.floor(remainingRows - depth - depth / remainingRows);
  const colSpan = (column.columns && column.columns.length) || 1;

  return {
    rowSpan: rowSpan,
    colSpan: colSpan,
  };
}
